import re
import binascii
import string
import base64

def cut_text(text,lenth):
    textArr = re.findall('.{'+str(lenth)+'}', text)
    textArr.append(text[(len(textArr)*lenth):])
    return textArr
text = open('6.txt')
encode = text.read()
#encode = base64.b64decode(encode)
#encode = encode.decode("utf-8")
encode = ''
#encode=''
litencode=encode.lower()
lisencode=cut_text(litencode,14)
msgs=list(map(binascii.unhexlify,lisencode))

#print(msgs)

letters = string.ascii_letters.encode('ascii')

def bxor(a, b):     
    if len(a) > len(b):
        return bytes([x ^ y for x, y in zip(a[:len(b)], b)])
    else:
        return bytes([x ^ y for x, y in zip(a, b[:len(a)])])

key = [0] *7
for a in msgs:
    possible_spaces = [0] * len(a)
    for b in msgs:
        if a == b: continue
        c = bxor(a, b)
        for k in range(len(c)):
            if c[k] not in letters and c[k] != 0: continue

            possible_spaces[k] += 1

    precision = 8

    for s in range(len(possible_spaces)):
        if possible_spaces[s] < len(msgs)-precision: continue
        key[s] = a[s] ^ 0x20


result=''
print("key",key)
for text in msgs:
	t=bxor(key,text)
	result+=bytes.decode(t)
print('\033[1;34;40m')
print('\ntext:',result)
print('\033[0m')
